import { useState } from "react";

const Home = (props) => {
    const { warehouses, inventory, items, categories } = props;
    const [selectedWarehouseId, setSelectedWarehouseId] = useState(null);
    console.log(warehouses);
    // Function to render warehouse list
    const renderWarehouses = () => (
        <div id='allRows'>
            {warehouses.length ? warehouses.map((warehouse) => (            
                <div className="warehouses" key={warehouse.warehouseId}>
                    <div id="warehouseContainer">
                        <p>{warehouse.Name}</p>
                        <p>{warehouse.Location}</p>
                    </div>
                    <button onClick={() => setSelectedWarehouseId(warehouse.WarehouseId)}>VIEW INVENTORY</button>
                </div>
            )) : "No data available"}
        </div>
    );

    // Function to render inventory of the selected warehouse
    const renderInventory = () => {
        // Assuming `inventory` now includes item details and is linked to warehouses through WarehouseId
        const filteredInventory = inventory.filter(item => item.WarehouseId === selectedWarehouseId);
        return (
            <div>
                <h2>Inventory for Warehouse</h2>
                {filteredInventory.length ? filteredInventory.map((item) => (
                    <div key={item.InventoryId}>
                        <p>{item.Name}</p> {/* Assuming item details are merged in `inventory` prop */}
                        <p>Quantity: {item.Quantity}</p>
                        {/* Include other item properties as needed */}
                    </div>
                )) : "No inventory found for this warehouse."}
                <button onClick={() => setSelectedWarehouseId(null)}>BACK TO WAREHOUSES</button>
            </div>
        );
    };

    return (
        <div id="home">
            <div id="homeContainer">
                <h1 id="homeHeaderText">Warehouse List</h1>
                <div id="homeContents">
                    <div id="homeContentsSubheader">
                        <div id="homeContentsSubheaderTitles">
                            <span>WAREHOUSES</span>
                            <span>INVENTORY</span>
                        </div>
                        <input type="search" id="searchBar" placeholder="Search Inventory"></input>
                    </div>
                    {selectedWarehouseId ? renderInventory() : renderWarehouses()}
                </div>
            </div>
        </div>
    );
};

export default Home;













