import { useState, useEffect } from "react";

const Home = (props) => {
    const { warehouses, items } = props;
    const [selectedWarehouseId, setSelectedWarehouseId] = useState(null);
    const [displayItems, setDisplayItems] = useState([]);

    console.log(warehouses)
    console.log(items)
    useEffect(() => {
        // Filter items based on the selectedWarehouseId
        if (selectedWarehouseId) {
            const updatedItems = items.filter((item) => item.warehouseid === selectedWarehouseId);
            setDisplayItems(updatedItems);
        } else {
            setDisplayItems([]); // Clear displayed items when no warehouse is selected
        }
    }, [selectedWarehouseId, items]); // Add items to dependency array to react to changes in items

    const renderWarehouses = () => (
        <div>
            {warehouses.length ? warehouses.map((warehouse) => (
                <div className="warehousesOrInventory" key={warehouse.warehouseId}>
                    <div className="warehouseInfoContainer">
                        <p>{warehouse.city}</p>
                        <p>{warehouse.address}</p>
                    </div>
                    <div className="viewInventoryButtonContainer">
                        <button className="viewInventoryButton" onClick={() => setSelectedWarehouseId(warehouse.id)}>VIEW INVENTORY</button>
                    </div>
                </div>
            )) : "No data available"}
        </div>
    );

    const renderInventory = () => (
        <div>
            <div id="addItemOrBackToWarehousesButtonContainer">
                <button className="addItemOrBackToWarehousesButton" onClick={() => setSelectedWarehouseId(null)}>BACK TO WAREHOUSES</button>
            </div>
            {displayItems.length ? displayItems.map((item) => (
                <div className="warehousesOrInventory" key={item.itemId}>
                    <div className="inventoryContainer">
                        <p>Item Name:<br/>{item.name}</p>
                        <p>Quantity:<br/>{item.quantity}</p>
                        <p>Category:<br/>{item.category}</p>
                    </div>
                </div>
            )) : "No items found for this warehouse."}
        </div>
    );

    return (
        <div id="home">
            <div id="homeContainer">
                <h1 id="homeHeaderText">Warehouse List</h1>
                <div id="homeContents">
                    <div id="homeContentsSubheader">
                        <div id="homeContentsSubheaderTitles">
                            <span className={selectedWarehouseId == null ? "underlineMe" : ""}>WAREHOUSES</span>
                            <span className={selectedWarehouseId != null ? "underlineMe" : ""}>INVENTORY</span>
                        </div>
                        <input type="search" id="searchBar" placeholder="Search Inventory"></input>
                    </div>
                    <div id="allRows">
                        {selectedWarehouseId ? renderInventory() : renderWarehouses()}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Home;












