import { useState } from "react";

const Home = (props) => {
    const { warehouses, inventory, items, categories, setInventory } = props;
    const [selectedWarehouseId, setSelectedWarehouseId] = useState(null);
console.log(inventory)
    const editQuantity = async (inventoryId, itemId, warehouseId) => {
        const newQuantity = prompt('Enter new quantity:');
        const newItemName = prompt('Enter new item name:')
        if (newQuantity) {
            try {
                const response = await fetch(`http://localhost:8282/api/inventory/${inventoryId}`, {
                    method: 'PUT', 
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ 
                        inventoryId: inventoryId,
                        itemId: itemId,
                        warehouseId: warehouseId,
                        quantity: newQuantity 
                    }), 
                });
                if (response.ok) {
                    const updatedInventory = inventory.map(item => {
                        if (item.inventoryId === inventoryId) {
                            return { ...item, quantity: newQuantity };
                        }
                        return item;
                    });
                    setInventory(updatedInventory);
                    console.log('Item updated successfully');
                } else {
                    console.error('Failed to update item');
                }
            } catch (error) {
                console.error('Error:', error);
            }
        }
    };


    const deleteItem = async (inventoryId) => {
        try {
            const response = await fetch(`http://localhost:8282/api/inventory/${inventoryId}`, {
                method: 'DELETE',
            });
            if (response.ok) {
                const newInventory = inventory.filter(item => item.inventoryId !== inventoryId);
                setInventory(newInventory);
                console.log('Item deleted successfully');
            } else {
                console.error('Failed to delete item');
            }
        } catch (error) {
            console.error('Error:', error);
        }
    };


    // Function to render warehouse list
    const renderWarehouses = () => (
        <div>
            {warehouses.length ? warehouses.map((warehouse) => (            
                <div className="warehousesOrInventory" key={warehouse.id}>
                    <div className="warehouseInfoContainer">
                        <p>{warehouse.name}</p>
                        <p>{warehouse.location}</p>
                    </div>
                    <div className="viewInventoryButtonContainer">
                        <button className="viewInventoryButton" onClick={() => setSelectedWarehouseId(warehouse.id)}>VIEW INVENTORY</button>
                    </div>
                </div>
            )) : "No data available"}
        </div>
    );

    // Function to render inventory of the selected warehouse
    const renderInventory = () => {
        const filteredInventory = inventory.filter(inventoryElement => inventoryElement.warehouseId === selectedWarehouseId);
        return (
            <div>
                <div id="addItemOrBackToWarehousesButtonContainer">
                    <button className="addItemOrBackToWarehousesButton" onClick={() => setSelectedWarehouseId(null)}>BACK TO WAREHOUSES</button>
                    <button className="addItemOrBackToWarehousesButton" onClick={() => setSelectedWarehouseId(null)}>BACK TO WAREHOUSES</button>
                </div>
                {filteredInventory.length ? filteredInventory.map((inventoryElement) => {
                    // Find the item in the items array that matches the current inventory element's itemId
                    const matchingItem = items.find(item => item.id === inventoryElement.itemId);
                    // If a matching item is found, use its name, otherwise indicate that the item is not found
                    const itemName = matchingItem ? matchingItem.name : 'Item name not found';

                    // Find the item in the items array that matches the current inventory element's itemId
                    const matchingCategory = categories.find(category => category.id === matchingItem.categoryId);
                    // If a matching item is found, use its name, otherwise indicate that the item is not found
                    const categoryName = matchingCategory ? matchingCategory.name : 'Category name not found';

                    return (
                        <div className="warehousesOrInventory" key={inventoryElement.inventoryId}>
                            <div className="inventoryContainer">
                                <p>Item #:<br/>{inventoryElement.itemId}</p>
                                <p>Quantity:<br/>{inventoryElement.quantity}</p>
                                <p>Item Name:<br/>{itemName}</p>
                                <p>Category Name:<br/>{categoryName}</p>
                            </div>
                            <div className="inventoryButtonContainer">
                                <div className="inventoryButtonContainerContainer">
                                    <button className="inventoryButtons" onClick={() => editQuantity(inventoryElement.inventoryId, inventoryElement.itemId, inventoryElement.warehouseId)}>EDIT QUANTITY</button>
                                    <button className="inventoryButtons" onClick={() => deleteItem(inventoryElement.inventoryId)}>DELETE ITEM</button>
                                </div>
                            </div>
                        </div>
                    );
                }) : "No inventory found for this warehouse."
                }
            </div>
        );
    };

    return (
        <div id="home">
            <div id="homeContainer">
                <h1 id="homeHeaderText">Warehouse List</h1>
                <div id="homeContents">
                    <div id="homeContentsSubheader">
                        <div id="homeContentsSubheaderTitles">
                            <span className={selectedWarehouseId == null ? "underlineMe" : ""}>WAREHOUSES</span>
                            <span className={selectedWarehouseId != null ? "underlineMe" : ""}>INVENTORY</span>
                        </div>
                        <input type="search" id="searchBar" placeholder="Search Inventory"></input>
                    </div>
                    <div id="allRows">
                        {selectedWarehouseId ? renderInventory() : renderWarehouses()}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Home;
